00600 bool XINTERPOLATOR::Update(XQWORD delta)
00601 {
00602   switch(type)
00603     {
00604       case XINTERPOLATOR_TYPE_LIMIT     : Interpolate(delta);
00605 
00606                                           if(mode == XINTERPOLATOR_LIMITMODE_INCREMENTAL)
00607                                             {
00608                                               if(target<=current) current=target;
00609                                             }
00610                                            else
00611                                             {
00612                                               if(mode == XINTERPOLATOR_LIMITMODE_DECREMENTAL)
00613                                               if(target>=current) current = target;
00614                                             }
00615 
00616                                           break;
00617 
00618       case XINTERPOLATOR_TYPE_RELATIVE  :
00619       case XINTERPOLATOR_TYPE_ABSOLUTE  : if(current > target) current = target;
00620                                           if(current < 0)      current = 0;
00621                                           break;
00622 
00623       case XINTERPOLATOR_TYPE_NONE      : break;
00624     }
00625 
00626   return true;
00627 }
