00641 bool XINTERPOLATOR::Interpolate(XQWORD delta)
00642 {
00643   switch(factortype)
00644     {
00645       case XINTERPOLATOR_FACTOR_RELATIVE  : return InterpolateRelative(delta);
00646       case XINTERPOLATOR_FACTOR_LINEAR    : return InterpolateLinear  (delta);
00647       case XINTERPOLATOR_FACTOR_NONE      :
00648                                 default   : break;
00649     }
00650 
00651   return true;
00652 }
