00666 bool XINTERPOLATOR::InterpolateLinear(XQWORD delta)
00667 {
00668   float   fdelta    = (float)(delta/100.0f);
00669   int     itarget   = (int)(target);
00670   float   result    = (float)current;
00671   int     margin    = 0;
00672 
00673   if(result ==  target) return false;
00674 
00675   if(result < itarget)
00676     {
00677       if (delta!=0)
00678         {
00679           if (carry<0.0000001f)  carry=0.0f;
00680 
00681           float inc = (speed*fdelta) + carry;
00682           carry = inc - float((int)inc);
00683 
00684           result += (int)inc;
00685         }
00686 
00687       if(result >= itarget)
00688         {
00689           result  = (float)itarget;
00690           carry   = 0.0f;
00691         }
00692 
00693     }
00694    else
00695     {
00696       if(delta!=0)
00697         {
00698           if(carry<0.0000001f) carry=0.0f;
00699 
00700           float inc=(speed*fdelta)+carry;
00701           carry=inc-float((int)inc);
00702 
00703           result -= (int)inc;
00704         }
00705 
00706       if(result <= float(itarget))
00707         {
00708           result = (float)itarget;
00709           carry  = 0.0f;
00710         }
00711     }
00712 
00713   current = (XDWORD)result;
00714 
00715   return true;
