00740     {
00741       if(delta!=0)
00742         {
00743           if(carry<0.0000001f) carry=0.0f;
00744 
00745           float dif = (float)(itarget - result);
00746           float inc = ((speed/100.0f)*fdelta*dif)+carry;
00747 
00748           carry   = inc - float((int)inc);
00749           result += (int)inc;
00750         }
00751 
00752       if(result > (itarget-margin))
00753         {
00754           result  = (float)itarget;
00755           carry   = 0.0f;
00756         }
00757     }
00758    else
00759     {
00760       if(delta!=0)
