00762           if (carry<0.0000001f) carry=0.0f;
00763 
00764           float dif = (float)(result-itarget);
00765           float inc = ((speed/100.0f)*fdelta*dif)+carry;
00766 
00767           carry   = inc - float((int)inc);
00768           result -= (int)inc;
00769         }
00770 
00771       if(result < float(itarget+margin))
00772         {
00773           result = (float)itarget;
