00826 bool XINTERPOLATOR::Serialize(XBUFFER* buffer)
00827 {
00828   XDWORD buffer_type,    buffer_target, buffer_mode,   buffer_factortype,buffer_max,      buffer_min,buffer_current,buffer_multiplier;
00829   float  buffer_speed;
00830 
00831   buffer->SetPosition(0);
00832 
00833   buffer->Get((XDWORD&)mask);
00834   buffer->Get((XDWORD&)buffer_type);
00835   buffer->Get((XDWORD&)buffer_mode);
00836   buffer->Get((XDWORD&)buffer_current);
00837   buffer->Get((XDWORD&)buffer_target);
00838   buffer->Get((float&)buffer_speed);
00839   buffer->Get((XDWORD&)buffer_max);
00840   buffer->Get((XDWORD&)buffer_min);
00841   buffer->Get((XDWORD&)buffer_multiplier);
00842   buffer->Get((XDWORD&)buffer_factortype);
00843 
00844   if(mask & XINTERPOLATOR_MASK_SPEED)      speed       = buffer_speed;
00845   if(mask & XINTERPOLATOR_MASK_MAXIMUN)    max         = buffer_max;
00846   if(mask & XINTERPOLATOR_MASK_MINIMUN)    min         = buffer_min;
00847   if(mask & XINTERPOLATOR_MASK_MULTIPLIER) multiplier  = buffer_multiplier;
00848   if(mask & XINTERPOLATOR_MASK_TYPE)       type        = (XINTERPOLATOR_TYPE)buffer_type;
00849   if(mask & XINTERPOLATOR_MASK_FACTOR)     factortype  = (XINTERPOLATOR_FACTOR)buffer_factortype;
00850   if(mask & XINTERPOLATOR_MASK_TARGET)     SetTarget(buffer_target);
00851   if(mask & XINTERPOLATOR_MASK_CURRENT)    SetCurrent(buffer_current);
00852 
00853   Update(0);
00854 
00855   return true;
00856 }
