00420 bool XLINUXSYSTEM::GetMemoryInfo(XDWORD& total,XDWORD& free)
00421 {
00422   struct sysinfo meminfo;
00423 
00424   if(sysinfo(&meminfo) == -1) return false;
00425 
00426   long long totalvirtualmem = meminfo.totalram;
00427   //Add other values in next statement to avoid int overflow on right hand side...
00428   //totalvirtualmem += meminfo.totalswap;
00429   totalvirtualmem *= meminfo.mem_unit;
00430 
00431   long long virtualmemused = meminfo.totalram - meminfo.freeram;
00432   //Add other values in next statement to avoid int overflow on right hand side...
00433   //virtualmemused += meminfo.totalswap - meminfo.freeswap;
00434   virtualmemused *= meminfo.mem_unit;
00435 
00436   total = (XDWORD)(totalvirtualmem                     / 1024);
00437   free  = (XDWORD)((totalvirtualmem - virtualmemused)  / 1024);
00438 
00439   return true;
00440 }
