00467 int XLINUXSYSTEM::GetCPUUsageTotal()
00468 {
00469   if(xmutexcheckCPUusage)
00470     {
00471       xmutexcheckCPUusage->Lock();
00472     }
00473 
00474   static unsigned int lasttotaluser, lasttotaluserlow, lasttotalsys, lasttotalidle;
00475   float               percent;
00476   int                 intpercent;     
00477   FILE*               file;
00478   unsigned long long  totaluser, totaluserlow, totalsys, totalidle, total;
00479      
00480   file = fopen("/proc/stat", "r");
00481   fscanf(file, "cpu %llu %llu %llu %llu", &totaluser, &totaluserlow, &totalsys, &totalidle);
00482   fclose(file);
00483      
00484   if(totaluser < lasttotaluser || totaluserlow < lasttotaluserlow || totalsys < lasttotalsys || totalidle < lasttotalidle)
00485     {
00486       // Overflow detection. Just skip this value.
00487       percent = -1.0;
00488     }
00489    else
00490     {
00491       total    = (totaluser - lasttotaluser) + (totaluserlow - lasttotaluserlow) + (totalsys - lasttotalsys);
00492       percent  = total * 100;
00493       total   += (totalidle - lasttotalidle);
00494       percent /= total;
00495     }
00496      
00497   lasttotaluser    = totaluser;
00498   lasttotaluserlow = totaluserlow;
00499   lasttotalsys     = totalsys;
00500   lasttotalidle    = totalidle;
00501      
00502   intpercent = percent;
00503 
00504   if(xmutexcheckCPUusage)
00505     {
00506       xmutexcheckCPUusage->UnLock();
00507     }
00508             
00509   return intpercent;
00510 }
