00524 int XLINUXSYSTEM::GetCPUUsageForProcessName(XCHAR* processname)
00525 {  
00526   XSTRING command;
00527   XSTRING result;
00528   XBUFFER charcmd;
00529   
00530   command.Format(__L("ps -C %s -o %%cpu"), processname);
00531 
00532   command.ConvertToASCII(charcmd);  
00533 
00534   // Open a pipe to the command
00535   FILE* pipe = popen((char*)charcmd.Get(), "r");
00536   if(!pipe) 
00537     {
00538        return 0; // Error
00539     }
00540    
00541   char buffer[128];    
00542   while(!feof(pipe)) 
00543     {
00544       if(fgets(buffer, 128, pipe) != NULL)
00545         {
00546           result += buffer;
00547         }
00548     }
00549   
00550   pclose(pipe);
00551 
00552   // Extract the CPU usage percentage from the result
00553   double cpupercentage = 0.0f;
00554   result.UnFormat(__L("%%CPU\n %lf\n"), &cpupercentage);
00555 
00556   return static_cast<int>(cpupercentage);
00557 }
