00702 bool XLINUXSYSTEM::ShutDown(XSYSTEM_CHANGESTATUSTYPE type)
00703 {
00704   int       opt_reboot = 0;
00705   int     fd;
00706 
00707   switch(type)
00708     {
00709       case XSYSTEM_CHANGESTATUSTYPE_REBOOT          : opt_reboot = 1;   break;     
00710       case XSYSTEM_CHANGESTATUSTYPE_POWEROFF        : opt_reboot = 0;   break;
00711                                         default     : break;
00712     }
00713 
00714   setpriority(PRIO_PROCESS, 0, PRIO_MIN);
00715                                                         
00716   whom = getlogin();
00717 
00718   setpriority(PRIO_PROCESS, 0, PRIO_MIN);
00719     signal(SIGINT,  int_handler);
00720     signal(SIGHUP,  int_handler);
00721     signal(SIGQUIT, int_handler);
00722     signal(SIGTERM, int_handler);
00723 
00724     chdir("/");
00725 
00726     signal(SIGPIPE, SIG_IGN);   
00727     signal(SIGINT,  SIG_IGN);   // now there's no turning back... 
00728 
00729     kill(1, SIGTSTP);             // tell init not to spawn more getty's 
00730         
00731     sync();
00732 
00733     signal(SIGTERM, SIG_IGN);
00734 
00735     if(fork() > 0) sleep(1000); // the parent will die soon... 
00736 
00737     setpgrp();                    // so the shell wont kill us in the fall 
00738     
00739   kill(-1, SIGTERM);          // a gentle kill of all other processes except init 
00740     sleep(2);
00741     
00742     kill(-1, SIGKILL);          // now use brute force... 
00743 
00744     acct(NULL);                 // turn off accounting 
00745 
00746     sync();
00747     sleep(2);
00748 
00749     swap_off();                 // remove swap files and partitions using swapoff 
00750 
00751     unmount_disks();              // unmount disks... 
00752 
00753     sync();
00754     sleep(1);
00755 
00756   if(opt_reboot) 
00757     {
00758           reboot(LINUX_REBOOT_CMD_RESTART);
00759       } 
00760    else 
00761     {
00762           reboot(LINUX_REBOOT_CMD_POWER_OFF);
00763     }         
00764 
00765   exit(0); // to quiet gcc 
00766 
00767   return true;
00768 }
