00876 void swap_off()
00877 {
00879     //  unmounted. It you don't have swapoff(1) or use mount to
00880     //  add swapspace, this may not be necessary, but I guess it
00881     //  won't hurt 
00882 
00883     int   pid;
00884     int   result;
00885     int   status;
00886 
00887     sync();
00888 
00889     if((pid = fork()) < 0) 
00890     {
00891           // Cannot fork for swapoff. Shrug!
00892           return;
00893       }
00894 
00895     if(!pid) 
00896     {
00897           execl("/sbin/swapoff" , SWAPOFF_ARGS, NULL);
00898           execl("/etc/swapoff"  , SWAPOFF_ARGS, NULL);
00899           execl("/bin/swapoff"  , SWAPOFF_ARGS, NULL);
00900 
00901           execlp("swapoff"      , SWAPOFF_ARGS, NULL);
00902 
00903           // Cannot exec swapoff, hoping umount will do the trick.
00904           exit(0);
00905       }
00906 
00907     while((result = wait(&status)) != -1 && result != pid);
00908 }
