00918 void unmount_disks()
00919 {
00920     // better to use umount directly because it may be smarter than us 
00921 
00922     int   pid;
00923     int   result;
00924     int   status;
00925 
00926     sync();
00927 
00928     if((pid = fork()) < 0) 
00929     {
00930           // Cannot fork for umount, trying manually.
00931           unmount_disks_ourselves();
00932           return;
00933       }
00934 
00935     if(!pid) 
00936     {
00937           execl(_PATH_UMOUNT, UMOUNT_ARGS, NULL);
00938 
00939         // Cannot exec %s, trying umount. _PATH_UMOUNT
00940 
00941         execlp("umount", UMOUNT_ARGS, NULL);
00942 
00943         // Cannot exec umount, giving up on umount.
00944           exit(0);
00945       }
00946 
00947     while ((result = wait(&status)) != -1 && result != pid);
00948 
00949     // Unmounting any remaining filesystems...
00950 
00951     unmount_disks_ourselves();
00952 }
