00954 
00962 void unmount_disks_ourselves()
00963 {
00964     // unmount all disks 
00965 
00966     FILE*           mtab;
00967     struct mntent*  mnt;
00968     char*           mntlist[128];
00969     int             i;
00970     int             n;
00971     char*           filesys;
00972     
00973     sync();
00974 
00975     if(!(mtab = setmntent(_PATH_MTAB, "r"))) 
00976     {
00977           // shutdown: Cannot open %s _PATH_MTAB;
00978           return;
00979       }
00980 
00981     n = 0;
00982     while (n < 100 && (mnt = getmntent(mtab))) 
00983     {
00984           mntlist[n++] = strdup(mnt->mnt_fsname[0] == '/' ?
00985             mnt->mnt_fsname : mnt->mnt_dir);
00986       }
00987 
00988     endmntent(mtab);
00989 
00990     // we are careful to do this in reverse order of the mtab file 
00991 
00992     for(i = n - 1; i >= 0; i--) 
00993     {
00994           filesys = mntlist[i];
00995 
00996           if(umount(mntlist[i]) < 0)
00997         {
00998                 // shutdown: Couldn't umount %s filesys
00999         }
01000       }
01001 }
