01049       XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_selem_register: error"));
01050       snd_mixer_close(handle);
01051       return false;
01052     }
01053 
01054   int ret = snd_mixer_load(handle);
01055   if(ret < 0) 
01056     {
01057       XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_load: error"));
01058       snd_mixer_close(handle);
01059       return false;
01060     }
01061 
01062   snd_mixer_selem_id_alloca(&sid);
01063 
01064   //sets simple-mixer index and name
01065   snd_mixer_selem_id_set_index(sid  , mix_index);
01066   snd_mixer_selem_id_set_name(sid   , mix_name1);
01067 
01068   elem = snd_mixer_find_selem(handle, sid);
01069   if(!elem) 
01070     {
01071       snd_mixer_selem_id_set_index(sid  , mix_index);
01072       snd_mixer_selem_id_set_name(sid   , mix_name2);
01073       
01074       elem = snd_mixer_find_selem(handle, sid);
01075       if(!elem) 
01076         {
01077           XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_find_selem: error"));
01078 
01079           snd_mixer_close(handle);
01080           return false;   
01081         }
01082     }
01083 
01084   long minv, maxv;
01085 
01086   snd_mixer_selem_get_playback_volume_range(elem, &minv, &maxv);
01087   
01088   XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Alsa volume] Volume range <%i,%i>"), minv, maxv);
01089 
01090   if(read) 
01091     {
01092       if(snd_mixer_selem_get_playback_volume(elem, (snd_mixer_selem_channel_id_t)0, level) < 0) 
01093         {
01094           XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_selem_get_playback_volume: error"));  
01095           snd_mixer_close(handle);
01096           return false;
01097         }
01098 
01099       XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Alsa volume] Get volume %i with status %i"), *level, ret);
01100 
01101       // make the value bound to 100 
01102       *level  -= minv;
01103       maxv    -= minv;
01104       minv     = 0;
01105      
01106       *level = 100 * (*level) / maxv; // make the value bound from 0 to 100
01107     }    
01108    else   
01109     {
01110       if(*level < 0 || *level > 100) // out of bounds
01111         {
01112           XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] out of bounds: error"));
01113           return false;
01114         }
01115 
01116       //minv = 0;
01117       //maxv = 100;
01118 
01119      
01120       *level = (*level * (maxv - minv) / (100-1)) + minv;
01121       
01122       if(snd_mixer_selem_set_playback_volume_range(elem, minv, maxv) < 0)
01123         {
01124           XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_selem_set_playback_volume_range: error"));
01125 
01126           snd_mixer_close(handle);
01127           return false;
01128         }
01129          
01130       if(snd_mixer_selem_set_playback_volume_all(elem, *level) < 0) 
01131         {
01132           XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_selem_set_playback_volume_all: error"));
01133 
01134           snd_mixer_close(handle);
01135           return false;
01136         }
01137    
01138       /*
01139       if(snd_mixer_selem_set_playback_volume(elem, (snd_mixer_selem_channel_id_t)0, *level) < 0) 
01140         {
01141           XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_selem_set_playback_volume: error"));
01142 
01143           snd_mixer_close(handle);
01144           return false;
01145         }
01146 
01147       if(snd_mixer_selem_set_playback_volume(elem, (snd_mixer_selem_channel_id_t)1, *level) < 0) 
01148         {
01149           XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[Alsa volume] snd_mixer_selem_set_playback_volume: error"));
01150 
01151           snd_mixer_close(handle);
01152           return false;
01153         }
01154        */
01155 
01156       XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Alsa volume] Set volume %i with status %i"), *level, ret);        
01157     }
01158 
01159   snd_mixer_close(handle);
01160 
01161   return true;
01162 
01163   #else
01164 
01165   return false;
01166 
01167   #endif   
01168 
01169   #endif 
