00673 bool XLOGBASE::AddEntry(XLOGLEVEL level, XCHAR* sectionID, bool inmemory, XBYTE* data, XDWORD size, XDWORD sizeline, bool showoffset, bool showtext)
00674 {
00675   if(!IsActive()) return false;
00676   if(!mutex)      return false;
00677 
00678   mutex->Lock();
00679 
00680   if(!PassFilters(level, sectionID))
00681     {
00682       mutex->UnLock();
00683       return false;
00684     }
00685 
00686   XSTRING    strdata;
00687   XDWORD      _size     = 0;
00688   XDWORD      _sizeline = sizeline;
00689   int        index     = 0;
00690   XDWORD     lines     = 0;
00691 
00692   while(_size<size)
00693     {
00694       XSTRING string;
00695 
00696       if(_sizeline > (size - _size)) _sizeline = (size - _size);
00697 
00698       if(showoffset)
00699         {
00700           strdata.Format(__L("%04X"),index);
00701           string += strdata;
00702           string += __L("   ");
00703         }
00704 
00705       for(XDWORD c=0; c<_sizeline; c++)
00706         {
00707           strdata.Format(__L("%02X "),data[index]);
00708           string += strdata;
00709           _size++;
00710           index++;
00711         }
00712 
00713       if(_sizeline != sizeline)
00714         {
00715           for(XDWORD c=0; c<(sizeline-_sizeline); c++)
00716             {
00717               strdata.Format(__L("   "));
00718               string += strdata;
00719             }
00720         }
00721 
00722       if(showtext)
00723         {
00724           index -= _sizeline;
00725           string += __L(" ");
00726 
00727           for(XDWORD c=0; c<_sizeline; c++)
00728             {
00729               XCHAR character = (XCHAR)data[index];
00730 
00731               if((character<0x20) || (character>0x80) || (character==__C('%')) || (character==__C('\\'))) character = __C('.');
00732 
00733               strdata.Format(__L("%c"),character);
00734               string += strdata;
00735               index++;
00736             }
00737         }
00738 
00739       mutex->UnLock();
00740 
00741       AddEntry(level, lines?NULL:sectionID, inmemory, string.Get());
00742 
00743       mutex->Lock();
00744 
00745       lines++;
00746     }
00747 
00748   mutex->UnLock();
00749 
00750   return true;
00751 }
