00846 bool XLOGBASE::CalculateInitialStatus()
00847 {
00848   XFILE* xfile = filelog->GetPrimaryFile();
00849   if(!xfile) return false;
00850 
00851   nlines  = 0;
00852   nentrys = 0;
00853   size    = xfile->GetSize();
00854 
00855   entrys.DeleteContents();
00856   entrys.DeleteAll();
00857 
00858   XDWORD             sizeBOM              = 0;
00859   XFILETXTFORMATCHAR formatchar           = filelog->GetFormatCharFromFile(&sizeBOM);
00860   int                sizebytescharacter   = filelog->SizeOfCharacter(formatchar);
00861 
00862   xfile->SetPosition(sizeBOM);
00863 
00864   bool    endfile;
00865   XDWORD  br;
00866   XBUFFER dataline;
00867   XBYTE*  readbuffer = new XBYTE[XFILETXT_MAXBUFFER];
00868   if(!readbuffer) return false;
00869 
00870   memset(readbuffer, 0, XFILETXT_MAXBUFFER);
00871 
00872   do{ XDWORD  bufferpos = 0;
00873 
00874       br      = XFILETXT_MAXBUFFER;
00875       endfile = !xfile->Read(readbuffer, &br);
00876       if(!br) break;
00877 
00878       do{ XFILETXTTYPELF typeLF;
00879           XDWORD         sizeLF   = 0;
00880           XDWORD         sizeline = 0;
00881           bool           endline = filelog->GetSizeOfLine(formatchar, &readbuffer[bufferpos], typeLF, sizeLF, sizeline, (br-bufferpos));
00882 
00883           if(endline)
00884             {
00885               if(readbuffer[bufferpos] != 0x20)
00886                 {
00887                   nentrys++;
00888 
00889                   lastentry = new XLOGENTRY();
00890                   if(lastentry)
00891                     {
00892                       lastentry->position = bufferpos;
00893                       entrys.Add(lastentry);
00894                     }
00895                 }
00896 
00897               if(lastentry) lastentry->size += (sizeline + sizeLF);
00898               nlines++;
00899 
00900               sizeline  *= sizebytescharacter;
00901               sizeline  += (sizeLF*sizebytescharacter);
00902               bufferpos += sizeline;
00903             }
00904            else
00905             {
00906               int lack = (br-bufferpos);
00907               bufferpos += lack;
00908 
00909               if(endfile && (bufferpos == br))
00910                 {
00911                   if(readbuffer[bufferpos] != 0x20)
00912                     {
00913                       nentrys++;
00914 
00915                       lastentry = new XLOGENTRY();
00916                       if(lastentry)
00917                         {
00918                           lastentry->position = bufferpos;
00919                           entrys.Add(lastentry);
00920                         }
00921                     }
00922 
00923                   if(lastentry) lastentry->size += lack;
00924                   nlines++;
00925                 }
00926             }
00927 
00928         } while(bufferpos < br);
00929 
00930     } while(!endfile);
00931 
00932   delete [] readbuffer;
00933 
00934   return true;
