01190         }
01191     }
01192    else
01193     {
01194       Backup_AdjustNFilesInCompressed();
01195     }
01196 
01197   if(iscompress)
01198     {
01199       XFILEZIP* xfilezip = new XFILEZIP();
01200       if(xfilezip)
01201         {
01202           XPATH xpathzipfile;
01203 
01204           Backup_CreateNameZIP(xpathzipfile);
01205 
01206           if(xfilezip->Open(xpathzipfile))
01207             {
01208               XPATH namefileinzip;
01209 
01210               xpathnamelog.GetNamefileExt(namefileinzip);
01211 
01212               if(!xfilezip->AddFile(xpathnamelog, namefileinzip)) status = false;
01213 
01214               xfilezip->Close();
01215             }
01216 
01217           delete xfilezip;
01218 
01219           status = xfilebackup->Erase(pathnamelog);
01220         }
01221     }
01222 
01223   GEN_XFACTORY.Delete_File(xfilebackup);
01224 
01225   return status;
01226 }
01227 
01228 
01238 bool XLOGBASE::Backup_ControlLimits()
01239 {
01240   if(!IsActive()) return false;
01241 
01242   if(!limit) return false;
01243 
01244   switch(typelimit)
01245     {
01246       case XLOGTYPELIMIT_NENTRYS  : if(nentrys<limit)   return false;
01247                                     break;
01248 
01249       case XLOGTYPELIMIT_SIZE     : if(size<limit)      return false;
01250                                     break;
01251       default: break;
01252     }
01253 
01254   Flush();
01255 
01256   bool status = true;
01257 
01258   if(backupisactive)
01259     {
01260       XPATH    xpathnamelog;
01261       XSTRING  datestr;
01262 
01263       //  If have backup DONT HAVE reduction
01264       //  The actual log file start
01265       xpathnamelog       = xpathfile.Get();
01266       backupxpathnamelog = xpathfile.Get();
01267 
01268       xdatetime->Read();
01269       datestr.Format(XLOG_CREATEMASKBACKUPFILE, xdatetime->GetYear()
01270                                               , xdatetime->GetMonth()
01271                                               , xdatetime->GetDay()
01272                                               , xdatetime->GetHours()
01273                                               , xdatetime->GetMinutes()
01274                                               , xdatetime->GetSeconds());
01275 
01276       backupxpathnamelog.AddToNameFile(true, datestr.Get());
01277 
01278       End(false);
01279 
01280       XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
01281       if(xfile)
01282         {
01283           xfile->Rename(xpathnamelog, backupxpathnamelog);
01284           GEN_XFACTORY.Delete_File(xfile);
01285         }
01286 
01287       status = Ini(xpathnamelog, NULL, isdumpintrace);
01288       if(status) status = Backup_AdjustNFiles(backupxpathnamelog.Get(), backupiscompress);
01289     }
01290    else
01291     {
01292       // Reduction control: NOT Backup.
01293 
01294       XDWORD nentrystoerase = nentrys - ((nentrys * reductionpercent) / 100);
01295       XQWORD sizetocopy = 0;
01296 
01297       for(int c=0; c<(int)nentrystoerase; c++)
01298         {
01299           XLOGENTRY* entry = entrys.Get(c);
01300           if(entry) sizetocopy += entry->size;
01301         }
01302 
01303       if(!sizetocopy) return false;
01304 
01305       filelog->GetPrimaryFile()->SetPosition(0);
01306       filelog->GetPrimaryFile()->EraseBlock(sizetocopy);
01307     }
01308 
01309   if(status) status = CalculateInitialStatus();
01310 
01311   return status;
01312 }
