01323 void XLOGBASE::Clean()
01324 {
01325   xdatetime        = NULL;
01326   xtimer           = NULL;
01327   mutex            = NULL;
01328   filelog          = NULL;
01329 
01330   isdumpintrace    = false;
01331 
01332   isactive         = false;
01333   typelimit        = XLOGTYPELIMIT_NONE;
01334 
01335   backupisactive   = false;
01336   backupmaxfiles   = 0;
01337   backupiscompress = false;
01338 
01339   limit            = 0;
01340   reductionpercent = XLOG_DEFAULTREDUCTIONPERCENT;
01341 
01342   lastsectionID.Empty();
01343 
01344   filtersectionsID.Empty();
01345   filterlevel      = XLOGLEVEL_ALL;
01346 
01347   nentrys          = 0;
01348   nlines           = 0;
01349   size             = 0;
01350   lastentry        = NULL;
01351 }
