00221 bool XMPINTEGER::Grow(XDWORD nblimbs)
00222 {
00223   XLIMB* newlimbs;
00224 
00225   if(nblimbs >  XMPINTEGER_MAXLIMBS) return false;
00226 
00227   if(GetNLimbs() < nblimbs)
00228     {
00229       newlimbs = (XLIMB*) new XBYTE[nblimbs * XMPINTEGER_CHARSINLIMB];
00230       if(!newlimbs) return false;
00231 
00232       memset(newlimbs, 0, nblimbs * XMPINTEGER_CHARSINLIMB );
00233 
00234       if(limbs)
00235         {
00236           memcpy(newlimbs, limbs, nlimbs * XMPINTEGER_CHARSINLIMB);
00237           memset(limbs, 0, nlimbs * XMPINTEGER_CHARSINLIMB);
00238           delete [] limbs;
00239         }
00240 
00241       nlimbs = nblimbs;
00242       limbs = newlimbs;
00243     }
00244 
00245   return true;
00246 }
