00307 bool XMPINTEGER::CopyFrom(XMPINTEGER* xmpinteger)
00308 {
00309   XDWORD c;
00310 
00311   if(this == xmpinteger) return true;
00312 
00313   if(!xmpinteger->GetLimbs())
00314     {
00315       End();
00316       return true;
00317     }
00318 
00319   for(c=xmpinteger->GetNLimbs()- 1; c>0; c--)
00320     {
00321       if(xmpinteger->GetLimbs()[c] != 0 ) break;
00322     }
00323 
00324   c++;
00325 
00326   this->SetSign(xmpinteger->GetSign());
00327 
00328   Grow(c);
00329 
00330   memset(GetLimbs(), 0, (GetNLimbs() * XMPINTEGER_CHARSINLIMB));
00331   memcpy( GetLimbs(), xmpinteger->GetLimbs(), (c * XMPINTEGER_CHARSINLIMB));
00332 
00333   return true;
00334 }
