00371 bool XMPINTEGER::LeftSet(int z)
00372 {
00373   if(!Grow(1)) return false;
00374 
00375   memset(GetLimbs(), 0, (GetNLimbs() * XMPINTEGER_CHARSINLIMB));
00376 
00377   limbs[0] = (XLIMB)(( z < 0 ) ?-z : z);
00378   sign     = (( z < 0 ) ? -1 : 1);
00379 
00380   return true;
