00395 int XMPINTEGER::GetBit(XDWORD position)
00396 {
00397   if(nlimbs * XMPINTEGER_BITSINLIMB <= position) return 0;
00398 
00399   return ( limbs[position / XMPINTEGER_BITSINLIMB] >> (position % XMPINTEGER_BITSINLIMB) ) & 0x01;
00400 }
