00415 bool XMPINTEGER::SetBit(XDWORD position, XBYTE value)
00416 {
00417   XDWORD off = position / XMPINTEGER_BITSINLIMB;
00418   XDWORD idx = position % XMPINTEGER_BITSINLIMB;
00419 
00420   if((value != 0) && (value != 1)) return false;
00421 
00422   if(nlimbs * XMPINTEGER_BITSINLIMB <= position)
00423     {
00424       if(value == 0) return 0;
00425 
00426       if(!Grow(off + 1)) return false;
00427     }
00428 
00429   limbs[off] &= ~((XLIMB)(0x01)  << idx);
00430   limbs[off] |=  ((XLIMB)(value) << idx);
00431 
00432   return true;
00433 }
