00472 int XMPINTEGER::GetMSB()
00473 {
00474   XDWORD i;
00475   XDWORD j;
00476 
00477   for(i=nlimbs-1; i>0; i--)
00478     {
00479       if(limbs[i] != 0) break;
00480     }
00481 
00482   for(j = XMPINTEGER_BITSINLIMB; j>0; j--)
00483     {
00484       if(((limbs[i] >> ( j - 1 ) ) & 1 ) != 0 ) break;
00485     }
00486 
00487    return ((i * XMPINTEGER_BITSINLIMB) + j);
