00518 int XMPINTEGER::ImportFromBinary(XBYTE* buffer, XDWORD size)
00519 {
00520   XDWORD i;
00521   XDWORD j;
00522   XDWORD n;
00523 
00524   for(n=0; n<size; n++)
00525     {
00526       if(buffer[n] != 0) break;
00527     }
00528 
00529   if(Grow(XMPINTEGER_CHARSTOLIMBS(size- n)))
00530     {
00531       if(!LeftSet(0)) return false;
00532 
00533     } else return false;
00534 
00535   for(i=size, j=0; i>nlimbs; i--, j++ )
00536     {
00537       limbs[j / XMPINTEGER_CHARSINLIMB] |= ((XLIMB) buffer[i-1]) << ((j % XMPINTEGER_CHARSINLIMB) << 3);
00538     }
