00588 bool XMPINTEGER::LeftShift(XDWORD count)
00589 {
00590   XDWORD i;
00591   XDWORD v0;
00592   XDWORD t1;
00593   XLIMB  r0 = 0;
00594   XLIMB  r1;
00595 
00596   v0 = count / (XMPINTEGER_BITSINLIMB);
00597   t1 = count & (XMPINTEGER_BITSINLIMB - 1);
00598 
00599   i = GetMSB() + count;
00600 
00601   if(nlimbs * XMPINTEGER_BITSINLIMB < i)
00602     {
00603       if(!Grow(XMPINTEGER_BITSTOLIMBS(i))) return false;
00604     }
00605 
00606 
00607   // shift by count / limb_size
00608   if(v0>0)
00609     {
00610       for( i=nlimbs; i>v0; i--)
00611         {
00612           limbs[i - 1] = limbs[i - v0 - 1];
00613         }
00614 
00615       for( ; i > 0; i-- )
00616         {
00617           limbs[i - 1] = 0;
00618         }
00619     }
00620 
00621   // shift by count % limb_size
00622   if(t1 > 0)
00623     {
00624       for( i = v0; i < nlimbs; i++ )
00625         {
00626           r1 = limbs[i] >> (XMPINTEGER_BITSINLIMB - t1);
00627           limbs[i] <<= t1;
00628           limbs[i] |= r0;
00629           r0 = r1;
00630         }
00631     }
00632 
00633   return true;
00634 }
