00648 bool XMPINTEGER::RightShift(XDWORD count)
00649 {
00650   XDWORD i;
00651   XDWORD v0;
00652   XDWORD v1;
00653   XLIMB  r0 = 0;
00654   XLIMB  r1;
00655 
00656   v0 = count /  XMPINTEGER_BITSINLIMB;
00657   v1 = count & (XMPINTEGER_BITSINLIMB - 1);
00658 
00659   if((v0 > nlimbs) || (v0 == nlimbs && v1 > 0)) return LeftSet(0);
00660 
00661   // shift by count / limb_size
00662   if(v0>0)
00663     {
00664       for(i=0; i<nlimbs-v0; i++)
00665         {
00666           limbs[i] = limbs[i + v0];
00667         }
00668 
00669       for( ; i<nlimbs; i++)
00670         {
00671           limbs[i] = 0;
00672         }
00673     }
00674 
00675   // shift by count % limb_size
00676   if(v1>0)
00677     {
00678       for(i=nlimbs; i>0; i--)
00679         {
00680           r1 = limbs[i - 1] << (XMPINTEGER_BITSINLIMB - v1);
00681           limbs[i - 1] >>= v1;
00682           limbs[i - 1] |= r0;
00683           r0 = r1;
00684         }
00685     }
00686 
00687   return true;
00688 }
