00702 int XMPINTEGER::CompareABSValues(XMPINTEGER& xmpinteger)
00703 {
00704   XDWORD i;
00705   XDWORD j;
00706 
00707   for(i=nlimbs; i>0; i--)
00708     {
00709       if(limbs[i - 1] != 0) break;
00710     }
00711 
00712   for(j = xmpinteger.GetNLimbs(); j>0; j--)
00713     {
00714       if(xmpinteger.GetLimbs()[j - 1] != 0) break;
00715     }
00716 
00717   if((i == 0) && (j == 0)) return 0;
00718 
00719   if(i > j) return  1;
00720   if(j > i) return -1;
00721 
00722   for( ; i>0; i--)
00723     {
00724       if(limbs[i - 1] > xmpinteger.GetLimbs()[i - 1] ) return  1;
00725       if(limbs[i - 1] < xmpinteger.GetLimbs()[i - 1] ) return -1;
00726     }
00727 
00728   return 0;
00729 }
