00743 int XMPINTEGER::CompareSignedValues(XMPINTEGER& xmpinteger)
00744 {
00745   XDWORD i;
00746   XDWORD j;
00747 
00748   for(i=nlimbs; i>0; i--)
00749     {
00750       if( limbs[i - 1] != 0) break;
00751     }
00752 
00753   for(j=xmpinteger.GetNLimbs(); j>0; j--)
00754     {
00755       if(xmpinteger.GetLimbs()[j - 1] != 0) break;
00756     }
00757 
00758   if((i == 0) && (j == 0))  return 0;
00759 
00760   if( i > j ) return  GetSign();
00761   if( j > i ) return  -xmpinteger.GetSign();
00762 
00763   if((GetSign() > 0) && xmpinteger.GetSign() < 0 )    return  1;
00764   if((xmpinteger.GetSign() > 0) &&  (GetSign() < 0))  return -1;
00765 
00766   for( ; i > 0; i-- )
00767     {
00768       if(limbs[i - 1] > xmpinteger.GetLimbs()[i - 1] ) return  GetSign();
00769       if(limbs[i - 1] < xmpinteger.GetLimbs()[i - 1] ) return -GetSign();
00770     }
00771 
00772   return 0;
00773 }
