00815 bool XMPINTEGER::AdditionABS(XMPINTEGER* xmpinteger1, XMPINTEGER* xmpinteger2)
00816 {
00817   int    i;
00818   int    j;
00819   XLIMB* o;
00820   XLIMB* p;
00821   XLIMB  c;
00822 
00823   if( this == xmpinteger2)
00824     {
00825       XMPINTEGER* tempoxmpinteger = xmpinteger1;
00826 
00827       xmpinteger1 = this;
00828       xmpinteger2 = tempoxmpinteger;
00829     }
00830 
00831 
00832   if(this != xmpinteger1) CopyFrom(xmpinteger1);
00833 
00834   // X should always be positive as a result of unsigned additions.
00835 
00836   SetSign(1);
00837 
00838   for(j=xmpinteger2->GetNLimbs(); j>0; j--)
00839     {
00840       if(xmpinteger2->GetLimbs()[j - 1] != 0 ) break;
00841     }
00842 
00843   if(!Grow(j)) return false;
00844 
00845   o = xmpinteger2->GetLimbs();
00846   p = GetLimbs();
00847   c = 0;
00848 
00849   for(i=0; i<j; i++, o++, p++ )
00850     {
00851       *p +=  c; c  = ( *p <  c );
00852       *p += *o; c += ( *p < *o );
00853     }
00854 
00855   while(c != 0)
00856     {
00857       if( i >= (int)GetNLimbs())
00858         {
00859           if(!Grow(i + 1)) return false;
00860           p = GetLimbs() + i;
00861         }
00862 
00863       *p += c; c = ( *p < c ); i++; p++;
00864     }
00865 
00866   return true;
00867 }
