00927 bool XMPINTEGER::AdditionSigned(XMPINTEGER* xmpinteger1, XMPINTEGER* xmpinteger2)
00928 {
00929   int tempsign = xmpinteger1->GetSign();
00930 
00931   if((xmpinteger1->GetSign() * xmpinteger2->GetSign()) < 0)
00932     {
00933       if(xmpinteger1->CompareABSValues((*xmpinteger2)) >= 0)
00934         {
00935           if(!SubtractionABS(xmpinteger1, xmpinteger2)) return false;
00936           SetSign(tempsign);
00937         }
00938        else
00939         {
00940           if(!SubtractionABS(xmpinteger2, xmpinteger1)) return false;
00941           SetSign(-tempsign);
00942         }
00943     }
00944    else
00945     {
00946       if(!AdditionABS(xmpinteger1, xmpinteger2)) return false;
00947       SetSign(tempsign);
00948     }
00949 
00950   return true;
00951 }
