01059 bool XMPINTEGER::Multiplication(XMPINTEGER* xmpinteger1, XMPINTEGER* xmpinteger2)
01060 {
01061   int         i;
01062   int         j;
01063   XMPINTEGER  TA;
01064   XMPINTEGER  TB;
01065 //bool        status = true;
01066 
01067   TA.Ini();
01068   TB.Ini();
01069 
01070   if(this == xmpinteger1)
01071     {
01072       TA.CopyFrom(xmpinteger1);
01073       xmpinteger1 = &TA;
01074     }
01075 
01076   if(this == xmpinteger2)
01077     {
01078       TB.CopyFrom(xmpinteger2);
01079       xmpinteger2 = &TB;
01080     }
01081 
01082   for(i=xmpinteger1->GetNLimbs(); i>0; i--)
