01087   for(j=xmpinteger2->GetNLimbs(); j>0; j--)
01088     {
01089       if(xmpinteger2->GetLimbs()[j - 1] != 0) break;
01090     }
01091 
01092    if(!Grow(i + j)) return false;
01093    if(!LeftSet(0))  return false;
01094 
01095   for(i++; j > 0; j--)
01096     {
01097       MultiplicationHelper( i - 1, xmpinteger1->GetLimbs(), GetLimbs() + j - 1, xmpinteger2->GetLimbs()[j - 1]);
01098     }
01099 
01100   SetSign(xmpinteger1->GetSign() * xmpinteger2->GetSign());
01101 
01102   TA.End();
01103   TB.End();
01104 
01105   return true;
01106 }
01107 
01108 
01121 bool XMPINTEGER::Multiplication(XMPINTEGER* xmpinteger, int integer)
01122 {
01123   XMPINTEGER _xmpinteger;
01124   XLIMB*      pointer = new XLIMB();
01125 
01126   _xmpinteger.SetSign(1);
01127   _xmpinteger.SetNLimbs(1);
01128   _xmpinteger.SetLimbs(pointer);
01129   (*pointer) = integer;
01130 
01131   return Multiplication(xmpinteger, &_xmpinteger);
01132 }
01133 
01134 
01149 bool XMPINTEGER::Division(XMPINTEGER* xmpintegerQ, XMPINTEGER* xmpintegerR, XMPINTEGER* xmpintegerA, XMPINTEGER* xmpintegerB)
01150 {
01151   int i;
01152   int n;
01153   int t;
01154   int k;
01155 
01156   XMPINTEGER X;
01157   XMPINTEGER Y;
01158   XMPINTEGER Z;
01159   XMPINTEGER T1;
01160   XMPINTEGER T2;
01161 
01162   if(!xmpintegerB->CompareSignedValues(0)) return false;
01163 
01164   X.Ini();
01165   Y.Ini();
01166   Z.Ini();
01167 
01168   T1.Ini();
01169   T2.Ini();
01170 
01171   if(xmpintegerA->CompareABSValues((*xmpintegerB)) < 0 )
01172     {
01173       if( xmpintegerQ != NULL )
01174         {
01175           if(!xmpintegerQ->LeftSet(0)) return false;
01176         }
01177 
01178       if( xmpintegerR != NULL )
01179         {
01180           if(!xmpintegerR->CopyFrom(xmpintegerA)) return false;
01181         }
01182 
01183       return true;
01184     }
01185 
01186   X.CopyFrom(xmpintegerA);
01187   Y.CopyFrom(xmpintegerB);
01188 
01189   X.SetSign(1);
01190   Y.SetSign(1);
01191 
01192   if(!Z.Grow(xmpintegerA->GetNLimbs() + 2 )) return false;
01193   if(!Z.LeftSet(0)) return false;
01194   if(!T1.Grow(2))   return false;
01195   if(!T2.Grow(3))   return false;
01196 
01197   k = (Y.GetMSB() % XMPINTEGER_BITSINLIMB);
01198   if(k < (XMPINTEGER_BITSINLIMB - 1))
01199     {
01200       k = XMPINTEGER_BITSINLIMB - 1 - k;
01201 
01202       if(!X.LeftShift(k)) return false;
01203       if(!Y.LeftShift(k)) return false;
01204 
01205     } else k = 0;
01206 
01207   n = X.GetNLimbs() - 1;
01208   t = Y.GetNLimbs() - 1;
01209   if(!Y.LeftShift(XMPINTEGER_BITSINLIMB * (n - t))) return false;
01210 
01211   while(X.CompareSignedValues(Y) >= 0)
01212     {
01213        Z.GetLimbs()[n - t]++;
01214        if(!X.SubtractionSigned(&X, &Y)) return true;
01215     }
01216 
01217   Y.RightShift(XMPINTEGER_BITSINLIMB * (n - t));
01218 
01219   for( i = n; i > t ; i-- )
01220     {
01221       if( X.GetLimbs()[i] >= Y.GetLimbs()[t] )
01222         {
01223           Z.GetLimbs()[i - t - 1] = ~0;
01224         }
01225        else
01226         {
01227           XUDBL r;
01228 
01229           r  = (XUDBL) X.GetLimbs()[i] <<  XMPINTEGER_BITSINLIMB;
01230           r |= (XUDBL) X.GetLimbs()[i - 1];
01231           r /= Y.GetLimbs()[t];
01232 
01233           if(r>((XUDBL) 1 <<  XMPINTEGER_BITSINLIMB)-1)  r = ((XUDBL) 1 <<  XMPINTEGER_BITSINLIMB) - 1;
01234 
01235           Z.GetLimbs()[i - t - 1] = (XLIMB) r;
01236         }
01237 
01238       Z.GetLimbs()[i - t - 1]++;
01239 
01240       do{ Z.GetLimbs()[i - t - 1]--;
01241 
01242           if(!T1.LeftSet(0)) return false;
01243 
01244           T1.GetLimbs()[0] = (t < 1) ? 0 : Y.GetLimbs()[t - 1];
01245           T1.GetLimbs()[1] = Y.GetLimbs()[t];
01246 
01247           if(!T1.Multiplication(&T1, Z.GetLimbs()[i - t - 1])) return false;
01248 
01249           if(!T2.LeftSet(0)) return false;
01250 
01251           T2.GetLimbs()[0] = (i < 2) ? 0 : X.GetLimbs()[i - 2];
01252           T2.GetLimbs()[1] = (i < 1) ? 0 : X.GetLimbs()[i - 1];
01253           T2.GetLimbs()[2] = X.GetLimbs()[i];
01254 
01255         } while(T1.CompareSignedValues(T2) > 0 );
01256 
01257         if(!T1.Multiplication(&Y, Z.GetLimbs()[i - t - 1])) return false;
01258 
01259         if(!T1.LeftShift(XMPINTEGER_BITSINLIMB * (i - t - 1))) return false;
01260 
01261         if(!X.SubtractionSigned(&X, &T1 )) return false;
01262 
01263         if(X.CompareSignedValues(0) < 0 )
01264           {
01265             if(!T1.CopyFrom(&Y)) return false;
01266             if(!T1.LeftShift(XMPINTEGER_BITSINLIMB * (i - t - 1))) return false;
01267 
01268             if(!X.AdditionSigned(&X, &T1)) return false;
01269 
01270             Z.GetLimbs()[i - t - 1]--;
01271           }
01272     }
01273 
01274    if(xmpintegerQ != NULL )
01275     {
01276       if(!xmpintegerQ->CopyFrom(&Z)) return false;
01277       xmpintegerQ->SetSign(xmpintegerA->GetSign() * xmpintegerB->GetSign());
01278     }
01279 
01280    if(xmpintegerR != NULL )
01281     {
01282       if(!X.RightShift(k)) return false;
01283       X.SetSign(xmpintegerA->GetSign());
01284 
01285       if(!xmpintegerR->CopyFrom(&X)) return false;
01286 
01287       if(xmpintegerR->CompareSignedValues(0)==0)  xmpintegerR->SetSign(1);
01288     }
01289 
01290   X.End();
01291   Y.End();
01292   Z.End();
01293   T1.End();
01294   T2.End();
01295 
01296   return true;
01297 }
01298 
01299 
01314 bool XMPINTEGER::Division(XMPINTEGER* xmpintegerQ, XMPINTEGER* xmpintegerR, XMPINTEGER* xmpintegerA, int integerb)
01315 {
01316   XMPINTEGER _B;
01317   XLIMB*      pointer = new XLIMB();
01318 
01319   (*pointer) = ( integerb < 0 ) ? -integerb : integerb;
01320   _B.SetSign(( integerb < 0 ) ? -1 : 1);
01321   _B.SetNLimbs(1);
01322   _B.SetLimbs(pointer);
01323 
01324   return Division( xmpintegerQ, xmpintegerR, xmpintegerA, &_B );
01325 }
01326 
01327 
01341 bool XMPINTEGER::Module(XMPINTEGER* xmpintegerR, XMPINTEGER* xmpintegerA, XMPINTEGER* xmpintegerB)
01342 {
01343   if(xmpintegerB->CompareSignedValues(0) < 0) return false;
01344 
01345   if(!Division( NULL, xmpintegerR, xmpintegerA, xmpintegerB )) return false;
01346 
01347   while(xmpintegerR->CompareSignedValues(0) < 0)
01348     {
01349       if(!xmpintegerR->AdditionSigned(xmpintegerR, xmpintegerB)) return false;
01350     }
01351 
01352   while(xmpintegerR->CompareSignedValues((*xmpintegerB)) >= 0 )
01353     {
01354       if(!xmpintegerR->SubtractionSigned(xmpintegerR, xmpintegerB)) return false;
01355     }
01356 
01357   return true;
01358 }
01359 
01360 
01374 bool XMPINTEGER::Module(XLIMB* r, XMPINTEGER* xmpintegerA, int integerb)
01375 {
01376   XLIMB  x, y, z;
01377   XDWORD i;
01378 
01379   if(integerb == 0) return false;
01380   if(integerb  < 0) return false;
01381 
01382   // handle trivial cases
01383 
01384   if(integerb == 1 )
01385     {
01386       (*r) = 0;
01387       return true;
01388     }
01389 
01390   if(integerb == 2 )
01391     {
01392       (*r) = xmpintegerA->GetLimbs()[0] & 1;
01393       return true;
01394     }
01395 
01396   // general case
01397   for(i = xmpintegerA->GetNLimbs(), y=0; i>0; i--)
01398     {
01399       x  = xmpintegerA->GetLimbs()[i - 1];
01400       y  = ( y << XMPINTEGER_HALFLIMBSIZE ) | ( x >> XMPINTEGER_HALFLIMBSIZE );
01401       z  = y / integerb;
01402       y -= z * integerb;
01403 
01404       x <<= XMPINTEGER_HALFLIMBSIZE;
01405       y  = ( y << XMPINTEGER_HALFLIMBSIZE ) | ( x >> XMPINTEGER_HALFLIMBSIZE );
01406       z  = y / integerb;
01407       y -= z * integerb;
01408     }
01409 
01410   // If A is negative, then the current y represents a negative value. Flipping it to the positive side.
01411 
01412   if( xmpintegerA->GetSign() < 0 && y != 0 ) y = (integerb - y);
01413 
01414   *r = y;
01415 
01416   return true;
01417 }
01418 
01419 
01430 void XMPINTEGER::MontgomeryIni(XLIMB* mm, XMPINTEGER* N)
01431 {
01432   XLIMB x;
01433   XLIMB m0 = N->GetLimbs()[0];
01434 
01435   x  = m0;
01436   x += ( ( m0 + 2 ) & 4 ) << 1;
01437   x *= ( 2 - ( m0 * x ) );
01438 
01439   if( XMPINTEGER_BITSINLIMB >= 16 ) x *= ( 2 - ( m0 * x ) );
01440   if( XMPINTEGER_BITSINLIMB >= 32 ) x *= ( 2 - ( m0 * x ) );
01441   if( XMPINTEGER_BITSINLIMB >= 64 ) x *= ( 2 - ( m0 * x ) );
01442 
01443   *mm = ~x + 1;
01444 }
01445 
01446 
01460 void XMPINTEGER::MontgomeryMultiplication(XMPINTEGER* A, XMPINTEGER* B, XMPINTEGER* N, XLIMB mm, XMPINTEGER* T)
01461 {
01462   XLIMB   u0, u1, *d;
01463   XDWORD  i , n , m;
01464 
01465   memset( T->GetLimbs(), 0, T->GetNLimbs() * XMPINTEGER_CHARSINLIMB );
01466 
01467   d = T->GetLimbs();
01468   n = N->GetNLimbs();
01469   m = (B->GetNLimbs() < n) ? B->GetNLimbs() : n;
01470 
01471   for(i=0; i<n; i++)
01472     {
01473       // T = (T + u0*B + u1*N) / 2^XMPINTEGER_BITSINLIMB
01474 
01475       u0 = A->GetLimbs()[i];
01476       u1 = ( d[0] + u0 * B->GetLimbs()[0] ) * mm;
01477 
01478       MultiplicationHelper( m, B->GetLimbs(), d, u0 );
01479       MultiplicationHelper( n, N->GetLimbs(), d, u1 );
01480 
01481        *d++ = u0;
01482         d[n + 1] = 0;
01483     }
01484 
01485   memcpy(A->GetLimbs(), d, (n + 1) * XMPINTEGER_CHARSINLIMB );
01486 
01487   if(A->CompareABSValues((*N))>= 0)
01488          SubtractionHelper(n, N->GetLimbs(), A->GetLimbs());
01489     else SubtractionHelper(n, A->GetLimbs(), T->GetLimbs());/* prevent timing attacks */
01490 }
01491 
01492 
01505 void XMPINTEGER::MontgomeryReduction(XMPINTEGER* A, XMPINTEGER* N, XLIMB mm, XMPINTEGER* T)
01506 {
01507   XLIMB*     z = new XLIMB();
01508   XMPINTEGER U;
01509 
01510   (*z) = 1;
01511 
01512   U.SetNLimbs(1);
01513   U.SetSign(1);
01514   U.SetLimbs(z);
01515 
01516   MontgomeryMultiplication( A, &U, N, mm, T );
01517 }
01518 
01519 
01534 bool XMPINTEGER::SlidingWindowExponentiation(XMPINTEGER* A, XMPINTEGER* E, XMPINTEGER* N, XMPINTEGER* _RR)
01535 {
01536   XDWORD      wbits, wsize, one = 1;
01537   XDWORD      i, j, nblimbs;
01538   XDWORD      bufsize, nbits;
01539   XLIMB       ei, mm, state;
01540   XMPINTEGER  RR;
01541   XMPINTEGER  T;
01542   XMPINTEGER  W[2 << XMPINTEGER_WINDOWSIZE];
01543   XMPINTEGER  Apos;
01544   int         neg;
01545 
01546   if(N->CompareSignedValues(0) < 0 || ( N->GetLimbs()[0] & 1 ) == 0 ) return false;
01547   if(E->CompareSignedValues(0) < 0)                                   return false;
01548 
01549   //Init temps and window size
01550 
01551   MontgomeryIni( &mm, N );
01552   RR.Ini();
01553   T.Ini();
01554   Apos.Ini();
01555 
01556   memset((XBYTE*)W, 0, sizeof(W));
01557 
01558   i = E->GetMSB();
01559 
01560   wsize = ( i > 671 ) ? 6 : ( i > 239 ) ? 5 :
01561           ( i >  79 ) ? 4 : ( i >  23 ) ? 3 : 1;
01562 
01563   if(wsize > XMPINTEGER_WINDOWSIZE) wsize = XMPINTEGER_WINDOWSIZE;
01564 
01565   j = N->GetNLimbs() + 1;
01566 
01567   if(!Grow(j))    return false;
01568   if(!W[1].Grow(j)) return false;
01569   if(!T.Grow(j*2))  return false;
01570 
01571   // Compensate for negative A (and correct at the end)
01572 
01573   neg = ( A->GetSign() == -1 );
01574   if(neg)
01575     {
01576       if(!Apos.CopyFrom(A)) return false;
01577       Apos.SetSign(1);
01578       A = &Apos;
01579     }
01580 
01581   // If 1st call, pre-compute R^2 mod N
01582 
01583   if( _RR == NULL || _RR->GetLimbs() == NULL )
01584     {
01585       if(!RR.LeftSet(1))                                            return false;
01586       if(!RR.LeftShift(N->GetNLimbs() * 2 * XMPINTEGER_BITSINLIMB)) return false;
01587       if(!RR.Module(&RR, &RR, N))                                   return false;
01588 
01589       _RR->CopyFrom(&RR);
01590 
01591       if(_RR != NULL) _RR->CopyFrom(&RR);
01592 
01593     } else RR.CopyFrom(_RR);
01594 
01595     // W[1] = A * R^2 * R^-1 mod N = A * R mod N
01596 
01597   if(A->CompareSignedValues((*N)) >= 0)
01598     {
01599       if(!Module( &W[1], A, N )) return false;
01600     }
01601    else
01602     {
01603       if(!W[1].CopyFrom(A)) return false;
01604     }
01605 
01606   MontgomeryMultiplication(&W[1], &RR, N, mm, &T);
01607 
01608     // X = R^2 * R^-1 mod N = R mod N
01609 
01610   if(!CopyFrom(&RR)) return false;
01611   MontgomeryReduction(this, N, mm, &T);
01612 
01613   if(wsize > 1)
01614     {
01615       // W[1 << (wsize - 1)] = W[1] ^ (wsize - 1)
01616 
01617       j =  one << (wsize - 1);
01618 
01619       if(!W[j].Grow(N->GetNLimbs() + 1))  return false;
01620 
01621       if(!W[j].CopyFrom(&W[1])) return false;
01622 
01623       for(i = 0; i < wsize - 1; i++)
01624         {
01625           MontgomeryMultiplication( &W[j], &W[j], N, mm, &T);
01626         }
01627 
01628       // W[i] = W[i - 1] * W[1]
01629 
01630       for(i = j + 1; i < (one << wsize); i++)
01631         {
01632           if(!W[i].Grow(N->GetNLimbs() + 1)) return false;
01633           if(!W[i].CopyFrom(&W[i-1]))        return false;
01634 
01635           MontgomeryMultiplication( &W[i], &W[1], N, mm, &T);
01636         }
01637     }
01638 
01639   nblimbs = E->GetNLimbs();
01640   bufsize = 0;
01641   nbits   = 0;
01642   wbits   = 0;
01643   state   = 0;
01644 
01645   while(1)
01646     {
01647       if(bufsize == 0)
01648         {
01649           if(nblimbs == 0) break;
01650           nblimbs--;
01651           bufsize = sizeof(XLIMB ) << 3;
01652         }
01653 
01654       bufsize--;
01655 
01656       ei = (E->GetLimbs()[nblimbs] >> bufsize) & 1;
01657 
01658       // skip leading 0s
01659       if(ei == 0 && state == 0) continue;
01660       if(ei == 0 && state == 1)
01661         {
01662           // out of window, square X
01663           MontgomeryMultiplication(this, this, N, mm, &T );
01664           continue;
01665         }
01666 
01667       // add ei to current window
01668       state = 2;
01669 
01670       nbits++;
01671       wbits |= (ei << (wsize - nbits));
01672 
01673       if(nbits == wsize)
01674         {
01675           // X = X^wsize R^-1 mod N
01676           for( i = 0; i < wsize; i++ )
01677             {
01678               MontgomeryMultiplication(this, this, N, mm, &T);
01679             }
01680 
01681           // X = X * W[wbits] R^-1 mod N
01682           MontgomeryMultiplication(this, &W[wbits], N, mm, &T);
01683 
01684           state--;
01685           nbits = 0;
01686           wbits = 0;
01687         }
01688     }
01689 
01690   // process the remaining bits
01691   for(i=0; i<nbits; i++)
01692     {
01693       MontgomeryMultiplication(this, this, N, mm, &T);
01694       wbits <<= 1;
01695       if((wbits & (one << wsize)) != 0) MontgomeryMultiplication(this, &W[1], N, mm, &T);
01696     }
01697 
01698   // X = A^E * R * R^-1 mod N = A^E mod N
01699   MontgomeryReduction(this, N, mm, &T);
01700 
01701   if(neg)
01702     {
01703       SetSign(-1);
01704       if(!AdditionSigned(N, this)) return false;
01705     }
01706 
01707   for(i = (one << (wsize - 1)); i < (one << wsize); i++)
01708     {
01709       W[i].End();
01710     }
01711 
01712   W[1].End();
01713   T.End();
01714   Apos.End();
01715 
01716   if(_RR == NULL) RR.End();
01717 
01718   return true;
01719 }
01720 
01721 
01735 bool XMPINTEGER::GreatestCommonDivisor(XMPINTEGER* G, XMPINTEGER* A, XMPINTEGER* B)
01736 {
01737   XDWORD      lz;
01738   XDWORD      lzt;
01739   XMPINTEGER  TG;
01740   XMPINTEGER  TA;
01741   XMPINTEGER  TB;
01742 
01743   TG.Ini();
01744   TA.Ini();
01745   TB.Ini();
01746 
01747   if(!TA.CopyFrom(A)) return false;
01748   if(!TB.CopyFrom(B)) return false;
01749 
01750   lz  = TA.GetLSB();
01751   lzt = TB.GetLSB();
01752 
01753   if(lzt<lz) lz = lzt;
01754 
01755   if(!TA.RightShift(lz)) return false;
01756   if(!TB.RightShift(lz)) return false;
01757 
01758   TA.SetSign(1);
01759   TB.SetSign(1);
01760 
01761   while(TA.CompareSignedValues(0) != 0)
01762     {
01763       if(!TA.RightShift(TA.GetLSB())) return false;
01764       if(!TB.RightShift(TB.GetLSB())) return false;
01765 
01766       if(TA.CompareSignedValues(TB) >= 0)
01767         {
01768           if(!TA.SubtractionABS(&TA, &TB)) return false;
01769           if(!TA.RightShift(1))            return false;
01770         }
01771        else
01772         {
01773           if(!TB.SubtractionABS(&TB, &TA)) return false;
01774           if(!TB.RightShift(1))            return false;
01775         }
01776     }
01777 
01778   if(!TB.LeftShift(lz)) return false;
01779   if(!G->CopyFrom(&TB)) return false;
01780 
01781   TG.End();
01782   TA.End();
01783   TB.End();
01784 
01785   return true;
01786 }
01787 
01788 
01802 int XMPINTEGER::FillRandom(XDWORD size, XMPINTEGER_FUNCRANDOM funcrandom, void* paramrandom)
01803 {
01804   if(!Grow(XMPINTEGER_CHARSTOLIMBS(size))) return false;
01805   if(!LeftSet(0)) return false;
01806 
01807   if(!funcrandom) return false;
01808   if(!funcrandom((unsigned char *)GetLimbs(), size, paramrandom)) return false;
01809 
01810   return true;
01811 }
01812 
01813 
01826 int XMPINTEGER::ModularInverse(XMPINTEGER* A, XMPINTEGER* N)
01827 {
01828   XMPINTEGER G;
01829   XMPINTEGER TA;
01830   XMPINTEGER TU;
01831   XMPINTEGER U1;
01832   XMPINTEGER U2;
01833   XMPINTEGER TB;
01834   XMPINTEGER TV;
01835   XMPINTEGER V1;
01836   XMPINTEGER V2;
01837 
01838   if(N->CompareSignedValues(0) <= 0) return false;
01839 
01840   TA.Ini();
01841   TU.Ini();
01842   U1.Ini();
01843   U2.Ini();
01844   G.Ini();
01845   TB.Ini();
01846   TV.Ini();
01847   V1.Ini();
01848   V2.Ini();
01849 
01850   if(!G.GreatestCommonDivisor( &G, A, N ))  return false;
01851 
01852   if(G.CompareSignedValues(1) != 0) return false;
01853 
01854   if(!TA.Module(&TA, A, N)) return false;
01855 
01856   if(!TU.CopyFrom(&TA)) return false;
01857   if(!TB.CopyFrom(N))   return false;
01858   if(!TV.CopyFrom(N))   return false;
01859 
01860   if(!U1.LeftSet(1))    return false;
01861   if(!U2.LeftSet(0))    return false;
01862   if(!V1.LeftSet(0))    return false;
01863   if(!V2.LeftSet(1))    return false;
01864 
01865   do{ while((TU.GetLimbs()[0] & 1 ) == 0)
01866         {
01867           if(!TU.RightShift(1)) return false;
01868 
01869           if((U1.GetLimbs()[0] & 1 )!= 0 || (U2.GetLimbs()[0] & 1 ) != 0)
01870             {
01871               if(!U1.AdditionSigned(&U1, &TB))    return false;
01872               if(!U2.SubtractionSigned(&U2, &TA)) return false;
01873             }
01874 
01875           if(!U1.RightShift(1)) return false;
01876           if(!U2.RightShift(1)) return false;
01877         }
01878 
01879       while((TV.GetLimbs()[0] & 1) == 0)
01880         {
01881           if(!TV.RightShift(1)) return false;
01882 
01883           if(((V1.GetLimbs()[0] & 1 ) != 0) || ((V2.GetLimbs()[0] & 1 ) != 0))
01884             {
01885                 if(!V1.AdditionSigned(&V1, &TB ))     return false;
01886                 if(!V2.SubtractionSigned(&V2, &TA ))  return false;
01887             }
01888 
01889             if(!V1.RightShift(1)) return false;
01890             if(!V2.RightShift(1)) return false;
01891         }
01892 
01893       if(TU.CompareSignedValues(TV) >= 0)
01894         {
01895           if(!TU.SubtractionSigned(&TU, &TV)) return false;
01896           if(!U1.SubtractionSigned(&U1, &V1)) return false;
01897           if(!U2.SubtractionSigned(&U2, &V2)) return false;
01898         }
01899        else
01900         {
01901           if(!TV.SubtractionSigned(&TV, &TU)) return false;
01902           if(!V1.SubtractionSigned(&V1, &U1)) return false;
01903           if(!V2.SubtractionSigned(&V2, &U2)) return false;
01904         }
01905 
01906     } while(TU.CompareSignedValues(0) != 0);
01907 
01908   while(V1.CompareSignedValues(0) < 0 )
01909     {
01910       if(!V1.AdditionSigned(&V1, N))  return false;
01911     }
01912 
01913   while(V1.CompareSignedValues((*N)) >= 0)
01914     {
01915       if(!V1.SubtractionSigned(&V1, N)) return false;
01916     }
01917 
01918   if(!CopyFrom(&V1)) return false;
01919 
01920   TA.End();
01921   TU.End();
01922   U1.End();
01923   U2.End();
01924   G.End();
01925   TB.End();
01926   TV.End();
01927   V1.End();
01928   V2.End();
01929 
01930   return true;
01931 }
01932 
01933 
01943 int XMPINTEGER::CheckSmallFactors()
01944 {
01945   XDWORD r;
01946   int    i;
01947   int    ret = 0;
01948 
01949   if((GetLimbs()[0] & 1 ) == 0) return -1;
01950 
01951   for(i=0; SmallPrime[i]>0; i++)
01952     {
01953       if(CompareSignedValues(SmallPrime[i] ) <= 0 ) return 1;
01954 
01955       if(!Module(&r, this, SmallPrime[i]))  return -1;
01956 
01957       if(r== 0) return -1;
01958     }
01959 
01960   return  ret;
01961 }
01962 
01963 
01977 bool XMPINTEGER::MillerRabin(XMPINTEGER* X, XMPINTEGER_FUNCRANDOM funcrandom, void* paramrandom)
01978 {
01979   XMPINTEGER  W;
01980   XMPINTEGER  R;
01981   XMPINTEGER  T;
01982   XMPINTEGER  A;
01983   XMPINTEGER  RR;
01984   int         i;
01985   int         j;
01986   int         n;
01987   int         s;
01988 
01989   W.Ini();
01990   R.Ini();
01991   T.Ini();
01992   A.Ini();
01993   RR.Ini();
01994 
01995   // W = |X| - 1
01996   // R = W >> lsb( W )
01997 
01998   if(!W.SubtractionSigned(this, 1)) return false;
01999 
02000   s = W.GetLSB();
02001 
02002   if(!R.CopyFrom(&W)) return false;
02003   if(!R.RightShift(s))  return false;
02004 
02005   i = GetMSB();
02006 
02007   // HAC, table 4.4
02008 
02009   n = ( ( i >= 1300 ) ?  2 : ( i >=  850 ) ?  3 :
02010         ( i >=  650 ) ?  4 : ( i >=  350 ) ?  8 :
02011         ( i >=  250 ) ? 12 : ( i >=  150 ) ? 18 : 27 );
02012 
02013   for(i=0; i<n; i++)
02014     {
02015       // pick a random A, 1 < A < |X| - 1
02016       if(!A.FillRandom(GetNLimbs()*XMPINTEGER_CHARSINLIMB, funcrandom, paramrandom))  return false;
02017 
02018       if(A.CompareSignedValues(W) >= 0)
02019         {
02020           j = A.GetMSB() - W.GetMSB();
02021           if(!A.RightShift(j + 1 )) return false;
02022         }
02023 
02024       A.GetLimbs()[0] |= 3;
02025 
02026       // A = A^R mod |X|
02027 
02028       if(!A.SlidingWindowExponentiation(&A, &R, this, &RR ))  return false;
02029 
02030       if(A.CompareSignedValues(W) == 0 || A.CompareSignedValues(1) == 0 )  continue;
02031 
02032       j = 1;
02033       while( j < s && A.CompareSignedValues(W) != 0 )
02034         {
02035           // A = A * A mod |X|
02036           if(!T.Multiplication(&A, &A ))  return false;
02037           if(!A.Module( &A, &T, this))    return false;
02038 
02039           if(A.CompareSignedValues(1) == 0 ) break;
02040 
02041           j++;
02042         }
02043 
02044       // not prime if A != |X| - 1 or A == 1
02045       if(A.CompareSignedValues(W) != 0 || (A.CompareSignedValues(1) == 0)) return false;
02046     }
02047 
02048   W.End();
02049   R.End();
02050   T.End();
02051   A.End();
02052   RR.End();
02053 
02054   return true;
02055 }
02056 
02057 
02070 int XMPINTEGER::IsPrime(XMPINTEGER_FUNCRANDOM funcrandom, void* paramrandom)
02071 {
02072  XMPINTEGER XX;
02073  int        ret;
02074 
02075  XX.CopyFrom(this);
02076 
02077  XX.SetSign(1);
02078  //XX.SetNLimbs(GetNLimbs());
02079  //XX.SetLimbs(GetLimbs());       // Abs(X)
02080 
02081  if(XX.CompareSignedValues(0) == 0 || XX.CompareSignedValues(1) == 0)  return -1;
02082 
02083  if(XX.CompareSignedValues(2) == 0) return 0;
02084 
02085  if((ret = XX.CheckSmallFactors())!= 0)
02086     {
02087       if(ret == 1) return 0;
02088       return ret;
02089     }
02090 
02091   return MillerRabin(&XX, funcrandom, paramrandom)?0:-1;
02092 }
02093 
02094 
02109 bool XMPINTEGER::GeneratePrime(int nbits, int dhflag, XMPINTEGER_FUNCRANDOM funcrandom, void* paramrandom)
02110 {
02111   int        k;
02112   int        n;
02113   XDWORD     r;
02114   XMPINTEGER Y;
02115 
02116   if( nbits < 3 || nbits > XMPINTEGER_MAXBITS) return false;
02117 
02118   Y.Ini();
02119 
02120   n = XMPINTEGER_BITSTOLIMBS(nbits);
02121 
02122   if(!FillRandom(n*XMPINTEGER_CHARSINLIMB, funcrandom, paramrandom)) return false;
02123 
02124   k = GetMSB();
02125   if(k<nbits)
02126     {
02127       if(!LeftShift(nbits-k))   return false;
02128     }
02129 
02130   if(k>nbits)
02131     {
02132       if(!RightShift(k-nbits))  return false;
02133     }
02134 
02135   GetLimbs()[0] |= 3;
02136 
02137   if(dhflag == 0)
02138     {
02139       int ret;
02140 
02141       while((ret=IsPrime(funcrandom, paramrandom))!= 0)
02142         {
02143           if( ret != -1)  return false;
02144           if(!AdditionSigned(this, 2)) return false;
02145         }
02146     }
02147    else
02148     {
02149       // An necessary condition for Y and X = 2Y + 1 to be prime is X = 2 mod 3 (which is equivalent to Y = 2 mod 3).
02150       // Make sure it is satisfied, while keeping X = 3 mod 4
02151 
02152       if(!Module( &r, this, 3)) return false;
02153 
02154       if(r == 0)
02155         {
02156           if(!AdditionSigned(this, 8))  return false;
02157         }
02158        else
02159         {
02160           if( r == 1 )
02161             {
02162               if(!AdditionSigned(this, 4 )) return false;
02163             }
02164         }
02165 
02166       // Set Y = (X-1) / 2, which is X / 2 because X is odd
02167       if(!Y.CopyFrom(this)) return false;
02168       if(!Y.RightShift(1))  return false;
02169 
02170       while(1)
02171         {
02172           int ret;
02173           // First, check small factors for X and Y before doing Miller-Rabin on any of them
02174 
02175           if((ret = CheckSmallFactors()) == 0 && (ret = Y.CheckSmallFactors()) == 0 &&  (ret = MillerRabin(this, funcrandom, paramrandom)) == 0 && (ret = MillerRabin(&Y, funcrandom, paramrandom)) == 0)  break;
02176 
02177 
02178           if(ret != -1) return false;
02179 
02180           // Next candidates. We want to preserve Y = (X-1) / 2 and Y = 1 mod 2 and Y = 2 mod 3 (eq X = 3 mod 4 and X = 2 mod 3)  so up Y by 6 and X by 12.
02181 
02182           if(!AdditionSigned(this, 12)) return false;
02183           if(!Y.AdditionSigned(&Y, 6))  return false;
02184         }
02185     }
02186 
02187   Y.End();
02188 
02189   return true;
02190 }
02191 
02192 
02205 bool XMPINTEGER::SetFromString(int radix, XCHAR* string)
02206 {
02207   int        i;
02208   int        j;
02209   int        slen;
02210   int        n;
02211   XDWORD     d;
02212   XMPINTEGER T;
02213 
02214   if((radix < 2) || (radix > 16)) return false;
02215 
02216   T.Ini();
02217 
02218   XSTRING _string;
02219 
02220   _string = string;
02221 
02222   slen = _string.GetSize();
02223 
02224   if(radix == 16)
02225     {
02226       n = XMPINTEGER_BITSTOLIMBS(slen << 2);
02227 
02228       if(!Grow(n))    return false;
02229       if(!LeftSet(0)) return false;
02230 
02231       for(i=slen, j=0; i>0; i--, j++ )
02232         {
02233           if(i==1 && string[i-1]==__C('-'))
02234             {
02235               SetSign(-1);
02236               break;
02237             }
02238 
02239           if(!GetDigit(&d, radix, string[i-1])) return false;
02240           GetLimbs()[j / (2 * XMPINTEGER_CHARSINLIMB)] |= d << ( (j % (2 * XMPINTEGER_CHARSINLIMB)) << 2 );
02241         }
02242     }
02243    else
02244     {
02245       if(!LeftSet(0)) return false;
02246 
02247       for(i=0; i<slen; i++)
02248         {
02249           if(i==0 && string[i]==__C('-'))
02250             {
02251               SetSign(-1);
02252               continue;
02253             }
02254 
02255           if(!GetDigit(&d, radix, string[i])) return false;
02256 
02257           if(!T.Multiplication(this, radix))  return false;
02258 
02259           if(GetSign()== 1)
02260             {
02261               if(!AdditionSigned(&T, d))    return false;
02262             }
02263            else
02264             {
02265               if(!SubtractionSigned(&T, d)) return false;
02266             }
02267         }
02268     }
02269 
02270   T.End();
02271 
02272   return true;
02273 }
02274 
02275 
02288 bool XMPINTEGER::SetFromString(int radix, XSTRING& string)
02289 {
02290   return SetFromString(radix, string.Get());
02291 }
02292 
02293 
02306 bool XMPINTEGER::GetToString(int radix, XSTRING& string)
02307 {
02308   XMPINTEGER  T;
02309   XSTRING     charshex;
02310 //int         n;
02311   
02312   charshex = __L("0123456789ABCDEF");
02313 
02314   string.Empty();
02315 
02316   if( radix < 2 || radix > 16 ) return false;
02317 
02318   /*
02319   n = GetMSB();
02320   if( radix >=  4 ) n >>= 1;
02321   if( radix >= 16 ) n >>= 1;
02322   n += 3;
02323   */
02324 
02325   T.Ini();
02326 
02327   if(GetSign() == -1) string.Add(__C('-'));
02328 
02329   if(radix == 16)
02330     {
02331       int c;
02332       int i;
02333       int j;
02334       int k;
02335 
02336       for(i = GetNLimbs(), k = 0; i > 0; i-- )
02337         {
02338          for(j=XMPINTEGER_CHARSINLIMB; j>0; j--)
02339             {
02340               XCHAR charhex;
02341 
02342               c=(GetLimbs()[i-1] >> ((j-1) << 3)) & 0xFF;
02343 
02344               if(c==0 && k==0 && (i+j+3)!= 0) continue;
02345 
02346               charhex = charshex.Get()[c / 16]; string.Add(charhex);
02347               charhex = charshex.Get()[c % 16]; string.Add(charhex);
02348 
02349               k=1;
02350             }
02351         }
02352     }
02353    else
02354     {
02355       if(!T.CopyFrom(this)) return false;
02356 
02357       if(T.GetSign() == -1) T.SetSign(1);
02358 
02359       if(!T.SetToStringHelper(radix, string)) return false;
02360     }
02361 
02362   T.End();
02363 
02364   return true;
02365 }
02366 
02367 
02379 bool XMPINTEGER::SetFromXBuffer(XBUFFER& xbuffer)
02380 {
02381   int i;
02382   int j;
02383   int n;
02384 
02385   for(n=0; n<(int)xbuffer.GetSize(); n++)
02386     {
02387       if(xbuffer.GetByte(n) != 0) break;
02388     }
02389 
02390   if(!Grow(XMPINTEGER_CHARSTOLIMBS(xbuffer.GetSize() - n ))) return false;
02391   if(!LeftSet(0)) return false;
02392 
02393   for(i = xbuffer.GetSize(), j=0; i>n; i--, j++)
02394     {
02395       GetLimbs()[j / XMPINTEGER_CHARSINLIMB] |= ((XLIMB)xbuffer.GetByte(i-1)) << ((j % XMPINTEGER_CHARSINLIMB) << 3);
02396     }
02397 
02398   return true;
02399 }
02400 
02401 
02414 bool XMPINTEGER::GetToXBuffer(XBUFFER& xbuffer, XDWORD size)
02415 {
02416   int     i;
02417   int     j;
02418   XDWORD  n;
02419 
02420   n = GetSize();
02421 
02422   if(size < n) return false;
02423 
02424   xbuffer.Resize(size);
02425 
02426   for(i=size-1, j=0; n>0; i--, j++, n--)
02427     {
02428       xbuffer.Set((XBYTE)(GetLimbs()[j / XMPINTEGER_CHARSINLIMB] >> ((j % XMPINTEGER_CHARSINLIMB) << 3)), i);
02429     }
02430 
02431   return true;
02432 }
02433 
02434 
02444 bool XMPINTEGER::End()
02445 {
02446   if(!limbs) return false;
02447 
02448   memset(limbs, 0, nlimbs* XMPINTEGER_CHARSINLIMB);
02449   delete [] limbs;
02450 
02451   sign    = 1;
02452   nlimbs  = 0;
02453   limbs   = NULL;
02454 
02455   return true;
02456 }
02457 
02458 
02470 void XMPINTEGER::SubtractionHelper(XDWORD n, XLIMB *s, XLIMB *d)
02471 {
02472   int    i;
02473   XLIMB  c = 0;
02474   XLIMB  z;
02475 
02476   for(i=(int)c; i<(int)n; i++, s++, d++)
02477     {
02478        z  = ( *d <  c );      *d -=  c;
02479        c  = ( *d < *s ) + z;  *d -= *s;
02480     }
02481 
02482   while( c != 0 )
02483     {
02484       z = ( *d < c ); *d -= c;
02485       c = z; i++; d++;
02486     }
02487 }
02488 
02489 
02502 void XMPINTEGER::MultiplicationHelper(XDWORD i, XLIMB* s, XLIMB* d, XLIMB b)
02503 {
02504   XLIMB c = 0;
02505   XLIMB t = 0;
02506 
02507   for(; i>= 16; i-= 16)
02508     {
02509       XMPINTEGER_MULADDC_INIT
02510 
02511       XMPINTEGER_MULADDC_CORE   XMPINTEGER_MULADDC_CORE
02512       XMPINTEGER_MULADDC_CORE   XMPINTEGER_MULADDC_CORE
02513       XMPINTEGER_MULADDC_CORE   XMPINTEGER_MULADDC_CORE
02514       XMPINTEGER_MULADDC_CORE   XMPINTEGER_MULADDC_CORE
02515 
02516       XMPINTEGER_MULADDC_CORE   XMPINTEGER_MULADDC_CORE
02517       XMPINTEGER_MULADDC_CORE   XMPINTEGER_MULADDC_CORE
02518       XMPINTEGER_MULADDC_CORE   XMPINTEGER_MULADDC_CORE
02519       XMPINTEGER_MULADDC_CORE   XMPINTEGER_MULADDC_CORE
02520 
02521       XMPINTEGER_MULADDC_STOP
02522     }
02523 
02524   for( ; i>=8; i-=8)
02525     {
02526       XMPINTEGER_MULADDC_INIT
02527 
02528       XMPINTEGER_MULADDC_CORE   XMPINTEGER_MULADDC_CORE
02529       XMPINTEGER_MULADDC_CORE   XMPINTEGER_MULADDC_CORE
02530 
02531       XMPINTEGER_MULADDC_CORE   XMPINTEGER_MULADDC_CORE
02532       XMPINTEGER_MULADDC_CORE   XMPINTEGER_MULADDC_CORE
02533 
02534       XMPINTEGER_MULADDC_STOP
02535     }
02536 
02537   for( ; i > 0; i-- )
02538     {
02539       XMPINTEGER_MULADDC_INIT
02540       XMPINTEGER_MULADDC_CORE
02541       XMPINTEGER_MULADDC_STOP
02542     }
02543 
02544   t++;
02545 
02546   do{  (*d) += c;
02547        c = ( *d < c );
02548        d++;
02549 
02550     } while( c != 0 );
02551 }
02552 
02553 
02567 bool XMPINTEGER::GetDigit(XDWORD* data, int radix, XCHAR character)
02568 {
02569   (*data) = 255;
02570 
02571   if(character >= 0x30 && character <= 0x39 ) (*data) = character - 0x30;
02572   if(character >= 0x41 && character <= 0x46 ) (*data) = character - 0x37;
02573   if(character >= 0x61 && character <= 0x66 ) (*data) = character - 0x57;
02574 
02575   if( (*data) >= (XDWORD)radix) return false;
02576 
02577   return true;
02578 }
02579 
02580 
02593 bool XMPINTEGER::SetToStringHelper(int radix, XSTRING& string)
02594 {
02595   XLIMB r;
02596 
02597   if(radix < 2 || radix > 16) return false;
02598 
02599   if(!Module(&r, this, radix)) return false;
02600   if(!Division(this, NULL, this, radix)) return false;
02601 
02602   if(CompareSignedValues(0)!= 0)
02603     {
02604       if(!SetToStringHelper(radix, string)) return false;
02605     }
02606 
02607   if(r<10)
02608          string.Add((XCHAR)(r + 0x30 ));
02609     else string.Add((XCHAR)(r + 0x37 ));
02610 
02611   return true;
02612 }
02613 
02614 
02623 void XMPINTEGER::Clean()
02624 {
02625   sign    = 0;
02626   nlimbs  = 0;
02627   limbs   = NULL;
02628 }
