00182 void* XMEMORY_CONTROL::Assign(XDWORD size, char* pathfile, int line)
00183 {
00184   if(!size) return NULL;
00185 
00186   Lock();
00187  
00188   void* ptr = NULL;
00189   ptr = malloc(size);
00190   if(!ptr)
00191     {
00192       UnLock();
00193       return NULL;
00194     }
00195 
00196   if(!RegisterAssign(ptr, size, pathfile, line))
00197     {
00198       // XTRACE_PRINTCOLOR(4, __L("[XMemory Control] ALERT: Make Malloc -> The memory allocation could not be registered!"));
00199     }
00200 
00201   nassigns++;
00202   maxnassigns    = __MAX(nassigns, maxnassigns);
00203 
00204   used          += size;
00205   maxused        = __MAX(used, maxused);
00206 
00207   UnLock();
00208 
00209   return ptr;
00210 }
