00222 void XMEMORY_CONTROL::Free(void* ptr)
00223 {
00224   if(!ptr) return;
00225 
00226   Lock();
00227 
00228   free(ptr);
00229 
00230   XDWORD size;
00231 
00232   if(!DeRegisterAssign(ptr, size))
00233     {
00234       // XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("[XMemory Control] ALERT: Make Free -> The memory allocation has not been registered!"));
00235     }
00236    else
00237     {
00238       nassigns--;
00239       used -= size;
00240     }
00241 
00242   UnLock();
00243 }
