00447 XDWORD XMEMORY_CONTROL::CRC32ForByte(XDWORD ini)
00448 {
00449   for(int c = 0; c<8; ++c)
00450     {
00451       ini = (ini & 1? 0: (XDWORD)0xEDB88320L) ^ ini >> 1;
00452     }
00453 
00454   return ini ^ (XDWORD)0xFF000000L;
00455 }
