00470 XDWORD XMEMORY_CONTROL::CRC32(XBYTE* data, XWORD size)
00471 {
00472   static XDWORD table[0x100];
00473   XDWORD        crc = 0;
00474 
00475   if(!*table)
00476     {
00477       for(XWORD c=0; c<0x100; ++c)
00478         {
00479           table[c] = CRC32ForByte(c);
00480         }
00481     }
00482 
00483   for(XDWORD c=0; c<size; ++c)
00484     {
00485       crc = table[(XBYTE)crc ^ ((XBYTE*)data)[c]] ^ crc >> 8;
00486     }
00487 
00488   return crc;
00489 }
