00506 bool XMEMORY_CONTROL::RegisterAssign(void* ptr, XDWORD size, char* pathfile, int line)
00507 {
00508   XDWORD index = 0;
00509 
00510   if(!SearchAssignIndex(true, ptr, index))
00511     {
00512       if(!ResizeAssignList())             return false;
00513       if(!SearchAssignIndex(true, NULL, index)) return false;
00514     }
00515 
00516   assignlist[index].ptr         = ptr;
00517   assignlist[index].size        = size;
00518 
00519   XDWORD sizenamemodule = (XDWORD)strlen(pathfile)+1;
00520   int    c              = (int)sizenamemodule;
00521 
00522   while(c>=0)
00523     {
00524       if((pathfile[c] == '\\')  || (pathfile[c] == '/')) break;
00525       c--;
00526     }
00527 
00528   sizenamemodule    -= c;
00529   if(sizenamemodule >= XMEMORY_CONTROL_MAXNAMEMODULESIZE) sizenamemodule = (XMEMORY_CONTROL_MAXNAMEMODULESIZE-1);
00530 
00531   memcpy(assignlist[index].namemodule, &pathfile[c+1], sizenamemodule);
00532 
00533   assignlist[index].linemodule  = line;
00534 
00535   return true;
00536 }
