00580 bool XMEMORY_CONTROL::ResizeAssignList()
00581 {
00582   nblockregisterelements++;
00583 
00584   XDWORD newnregisterelements = (nblockregisterelements * XMEMORY_CONTROL_MAXBLOCKASSIGN);
00585 
00586   assignlist = (XMEMORY_CONTROL_ASSIGN*)realloc(assignlist, (sizeof(XMEMORY_CONTROL_ASSIGN) * newnregisterelements));
00587   if(!assignlist)  return false;
00588 
00589   XDWORD sizeextra  = (sizeof(XMEMORY_CONTROL_ASSIGN) *  (newnregisterelements - nregisterelements));
00590 
00591   // Erase new block
00592   memset(&assignlist[nregisterelements], 0, sizeextra);
00593 
00594   nregisterelements = newnregisterelements;
00595 
