00612 bool XMEMORY_CONTROL::SearchAssignIndex(bool free, void* ptr, XDWORD& index)
00613 {
00614   XDWORD  start   = 0;
00615   XDWORD  counter = 0;
00616   XDWORD  limit   = 0;
00617   bool    found   = false;
00618 
00619   index   = 0;
00620 
00621   #ifdef MICROCONTROLLER
00622   start = 0;
00623   #else
00624   start = CRC32((XBYTE*)&ptr, sizeof(XDWORD));
00625   start &= (nregisterelements -1);
00626   #endif
00627 
00628   counter = start;
00629   if(counter >= nregisterelements-1)  counter = 0;
00630 
00631   limit = (nregisterelements-1);
00632 
00633   for(int c=0; c<2; c++)
00634     {
00635       while(counter < limit)
00636         {
00637           if(free)
00638             {
00639               if(!assignlist[counter].ptr)
00640                 {
00641                   index = counter;
00642                   found = true;
00643                   break;
00644                 }
00645             }
00646            else
00647             {
00648               if(assignlist[counter].ptr == ptr)
00649                 {
00650                   index = counter;
00651                   found = true;
00652                   break;
00653                 }
00654             }
00655 
00656           counter++;
00657         }
00658 
00659      if(found) return true;
00660 
00661      counter = 0;
00662      limit   = start;
00663    }
00664 
00665   return false;
