01079 bool XPATH::Slash_Add()
01080 {
01081   int           count[2] = { 0 , 0 };
01082   XCHAR* str             = Get();
01083   bool          status   = false;
01084 
01085   XDWORD c = 0;
01086 
01087   while(c < GetSize())
01088     {
01089       if(str[c]==__C('/'))  count[0]++;
01090       if(str[c]==__C('\\')) count[1]++;
01091 
01092       c++;
01093     }
01094 
01095   if(!Slash_HaveAtLast())
01096     {
01097       if(count[0]>count[1])
01098              Add("/");
01099         else Add("\\");
01100 
01101       Slash_Normalize();
01102 
01103       status = true;
01104     }
01105 
01106   return status;
01107 }
