01121 bool XPATH::Slash_Normalize(bool inverse)
01122 {
01123   if(IsEmpty()) return false;
01124 
01125   XCHAR* str = Get();
01126   int           c   = 0;
01127 
01128   while(str[c])
01129     {
01130       if(inverse)
01131        {
01132          if(str[c]==__C('/'))  str[c] = __C('\\');
01133        }
01134       else
01135        {
01136          if(str[c]==__C('\\')) str[c] = __C('/');
01137        }
01138 
01139       c++;
01140     }
01141 
01142   return true;
01143 }
