01329 bool XPATH::Split(XSTRING* drive, XPATH* xpath, XSTRING* name, XSTRING* ext)
01330 {
01331   XCHAR* pathstr = Get();
01332   int    idrive  = 0;
01333   int    iname   = 0;
01334   int    iext    = 0;
01335   int    c;
01336 
01337   if(IsEmpty()) return false;
01338   if(!pathstr)  return false;
01339 
01340   c = 0;
01341   while(pathstr[c])
01342     {
01343       if(pathstr[c]==__C(':'))
01344         {
01345           if(drive) Copy(0,c+1,(*drive));
01346 
01347           idrive = c+1;
01348           break;
01349         }
01350 
01351       c++;
01352     }
01353 
01354   c = (GetSize()-1);
01355   while(pathstr[c])
01356     {
01357       if((pathstr[c]==__C('\\')) || (pathstr[c]==__C('/')))
01358         {
01359           break;
01360         }
01361 
01362       if(pathstr[c]==__C('.'))
01363         {
01364           if(ext) Copy(c, GetSize(), (*ext));
01365 
01366           iext = c;
01367           break;
01368         }
01369 
01370       c--;
01371     }
01372 
01373   c = (iext)?iext:GetSize();
01374   while(c>=0)
01375     {
01376       if((!c) || pathstr[c]==__C('\\') || pathstr[c]==__C('/')  || pathstr[c]==__C(':'))
01377         {
01378           if(pathstr[c]==__C('\\') || pathstr[c]==__C('/') || pathstr[c]==__C(':')) c++;
01379 
01380           if(name) Copy(c, (iext)?iext:GetSize(), (*name));
01381 
01382           iname = c;
01383           break;
01384         }
01385 
01386       c--;
01387     }
01388 
01389   if(xpath && iname) Copy(idrive, iname, (*xpath));
01390 
01391   return true;
01392 }
