00441 bool XPATHSMANAGER::AdjustRootPathDefault(XCHAR* assetsdirname)
00442 { 
00443   XDIR* xdir;
00444   XPATH xpathroot;
00445   XPATH xpathtest;
00446   bool  founddir    = false;
00447   int   indexfound  = 0;
