00449 
00450   GEN_XFACTORY_CREATE(xdir, Create_Dir())
00451   if(!xdir) return false;
00452 
00453   GetPathOfSection(XPATHSMANAGERSECTIONTYPE_ROOT, xpathroot);
00454   xpathroot.Slash_Add();
00455 
00456   do{ indexfound = xpathroot.Find(__L("/"), false, indexfound);
00457       if(indexfound == XSTRING_NOTFOUND)   indexfound = xpathroot.Find(__L("\\"), false, indexfound);
00458 
00459       if(indexfound != XSTRING_NOTFOUND)
00460         {
00461           xpathroot.Copy(0, indexfound, xpathtest);
00462 
00463           xpathtest.Slash_Add();
00464           xpathtest.Add(assetsdirname);
00465 
00466           founddir = xdir->Exist(xpathtest);
00467           if(founddir)
00468             {
00469               AddPathSection(XPATHSMANAGERSECTIONTYPE_ROOT, xpathtest);
00470               status  = true;
00471               break;    
00472             }
00473 
00474           indexfound++;
00475         } 
00476 
00477     } while(indexfound != XSTRING_NOTFOUND);
00478 
00479   if(!founddir)
00480     {
00481       xpathtest = xpathroot;
00482       xpathtest.Slash_Add();
00483       xpathtest.Add(assetsdirname);
00484 
00485       if(xdir->Make(xpathtest, false))
00486         {
00487           AddPathSection(XPATHSMANAGERSECTIONTYPE_ROOT, xpathtest);
00488           status  = true;      
