00456 bool XSTM32DIR::ReconvertSearchData(XDIRELEMENT* searchelement, FILINFO* fileinfo)
00457 {
00458   if(!searchelement)  return false;
00459   if(!fileinfo)       return false;
00460 
00461   searchelement->GetNameFile()->Set(fileinfo->fname);
00462      
00463   XDATETIME*  datetime  = searchelement->GetDateTimeFile_Modificated();
00464   XWORD       date      = fileinfo->fdate;
00465   XWORD       time      = fileinfo->ftime;
00466   
00467   
00468   // bit31:25    Year origin from the 1980 (0..127, e.g. 37 for 2017)
00469   // bit24:21    Month (1..12)
00470   // bit20:16    Day of the month (1..31)
00471     
00472   // bit15:11    Hour (0..23)
00473   // bit10:5     Minute (0..59)
00474   // bit4:0      Second / 2 (0..29, e.g. 25 for 50)
00475   
00476   datetime->SetYear(((date >> 9) & 0x7F) + 1980);
00477   datetime->SetMonth((date >> 5) & 0x0F);
00478   datetime->SetDay((date & 0x1F));
00479   
00480   datetime->SetHours((time >> 11) & 0x1F);
00481   datetime->SetMinutes((time >> 5) & 0x3F);
00482   datetime->SetSeconds((time & 0x1F) * 2);
00483     
00484   if(fileinfo->fattrib & AM_DIR)
00485           searchelement->SetType(XDIRELEMENTTYPE_DIR);
00486     else  searchelement->SetType(XDIRELEMENTTYPE_FILE);
00487  
00488   return true;
00489 }
