00458 bool XSCHEDULERTASK::CheckCondition(XDATETIME* xdatetimeactual, XTIMER* xtimeractual)
00459 {
00460   if(!xdatetimeactual) return false;
00461 
00462   XQWORD timeactualsec   = xdatetimeactual->GetSeconsFromDate();
00463 
00464   if(xdatetimelimitstart.IsValidDate())
00465     {
00466       if(timeactualsec <  xdatetimelimitstart.GetSeconsFromDate())
00467         {
00468           isinvalidtimelimit = false;
00469 
00470           xtimeractual->Reset();
00471           return false;
00472         }
00473        else isinvalidtimelimit = true;
00474     }
00475 
00476   if(xdatetimelimitend.IsValidDate())
00477     {
00478       if(timeactualsec >  xdatetimelimitend.GetSeconsFromDate())
00479         {
00480           isinvalidtimelimit = false;
00481           return false;
00482 
00483         } else isinvalidtimelimit = true;
00484     }
00485 
00486   if(conditiondayweekmask != XSCHEDULER_DAYWEEK_NONE)
00487     {
00488       if(!(GetDayOfWeekMask(xdatetimeactual) & conditiondayweekmask))
00489         {
00490           xtimeractual->Reset();
00491           return false;
00492         }
00493     }
00494 
00495   if(!ncyclesactual)  return false;
00496 
00497   bool checkcadence = true;
00498   if(isstartimmediatelycycles)
00499     {
00500       if(!ncyclesmade)  checkcadence = false;
00501     }
00502 
00503   if(checkcadence)
00504     {
00505       if(xdatetimecadence.IsValidDate())
00506         {
00507           if(xtimeractual->GetMeasureSeconds() < xdatetimecadence.GetSeconsFromDate()) return false;
00508         }
00509     }
00510 
00511   if(ncyclesactual>0)  ncyclesactual--;
00512 
00513   xtimeractual->Reset();
00514 
00515   ncyclesmade++;
00516 
00517   return true;
00518 }
