00532 XBYTE XSCHEDULERTASK::GetDayOfWeekMask(XDATETIME* xtimeactual)
00533 {
00534   if(!xtimeactual) return XSCHEDULER_DAYWEEK_NONE;
00535 
00536   XBYTE mask = XSCHEDULER_DAYWEEK_NONE;
00537 
00538   switch(xtimeactual->GetDayOfWeek())
00539     {
00540       case XDATETIME_DAYWEEK_SUNDAY     : mask = XSCHEDULER_DAYWEEK_ISSUNDAY;     break;
00541       case XDATETIME_DAYWEEK_MONDAY     : mask = XSCHEDULER_DAYWEEK_ISMONDAY;     break;
00542       case XDATETIME_DAYWEEK_TUESDAY    : mask = XSCHEDULER_DAYWEEK_ISTUESDAY;    break;
00543       case XDATETIME_DAYWEEK_WEDNESDAY  : mask = XSCHEDULER_DAYWEEK_ISWEDNESDAY;  break;
00544       case XDATETIME_DAYWEEK_THURSDAY   : mask = XSCHEDULER_DAYWEEK_ISTHURSDAY;   break;
00545       case XDATETIME_DAYWEEK_FRIDAY     : mask = XSCHEDULER_DAYWEEK_ISFRIDAY;     break;
00546       case XDATETIME_DAYWEEK_SATURDAY   : mask = XSCHEDULER_DAYWEEK_ISSATURDAY;   break;
00547     }
00548 
00549   return mask;
00550 }
