00772 XSCHEDULERTASK* XSCHEDULER::Task_GetForID(XDWORD ID)
00773 {
00774   if(!xmutexscheduler) return NULL;
00775   if(tasks.IsEmpty())  return NULL;
00776 
00777   xmutexscheduler->Lock();
00778 
00779   for(XDWORD c=0;c<tasks.GetSize();c++)
00780     {
00781       XSCHEDULERTASK* task = tasks.Get(c);
00782       if(task)
00783         {
00784           if(task->GetID()==ID)
00785             {
00786               xmutexscheduler->UnLock();
00787               return task;
00788             }
00789         }
00790     }
00791 
00792   xmutexscheduler->UnLock();
00793 
00794   return NULL;
00795 }
