00943 void XSCHEDULER::ThreadScheduler(void* data)
00944 {
00945   XSCHEDULER* xscheduler = (XSCHEDULER*)data;
00946   if(!xscheduler)                      
00947     {
00948       return;
00949     }
00950 
00951   if(!xscheduler->xmutexscheduler)     
00952     {
00953       return;
00954     }
00955 
00956   if(!xscheduler->xdatetimeactual)     
00957     {
00958       return;
00959     }
00960 
00961   if(!xscheduler->isactive) 
00962     {
00963       return;
00964     }
00965 
00966   GEN_XSLEEP.MilliSeconds(10);
00967 
00968   xscheduler->xmutexscheduler->Lock();
00969 
00970   XSCHEDULERTASK* task;
00971 
00972   task = xscheduler->Task_Get(xscheduler->indextask);
00973   if(!task)
00974     {
00975       xscheduler->xmutexscheduler->UnLock();
00976       xscheduler->indextask = 0;
00977       return;
00978     }
00979 
00980   if(xscheduler->xdatetimeactual) 
00981     {
00982       xscheduler->xdatetimeactual->Read();
00983     }
00984 
00985   if(task->IsActive())
00986     {
00987       if(task->CheckCondition(xscheduler->xdatetimeactual, task->GetXTimer()))
00988         {
00989           xscheduler->indextask++;
00990           xscheduler->xmutexscheduler->UnLock();
00991 
00992           XSCHEDULER_XEVENT xevent(xscheduler, XEVENT_TYPE_SCHEDULER);
00993 
00994           xevent.SetScheduler(xscheduler);
00995           xevent.SetTask(task);
00996           xevent.SetDateTime(xscheduler->xdatetimeactual);
00997 
00998           xscheduler->PostEvent(&xevent);
00999                  
01000           return;
01001         }
01002     }
01003    
01004   xscheduler->indextask++;
01005   xscheduler->xmutexscheduler->UnLock();
01006 }
